// Helper functions
export const searchIndianFood = (query) => {
  const lowerQuery = query.toLowerCase();
  return indianFoodDb.filter(food => 
    food.name.toLowerCase().includes(lowerQuery) ||
    food.category.toLowerCase().includes(lowerQuery) ||
    food.region.toLowerCase().includes(lowerQuery)
  );
};

export const getFoodsByCategory = (category) => {
  return indianFoodDb.filter(food => food.category === category);
};

export const getFoodsByRegion = (region) => {
  return indianFoodDb.filter(food => food.region.toLowerCase().includes(region.toLowerCase()));
};

export const getAllCategories = () => {
  return [...new Set(indianFoodDb.map(food => food.category))];
};

export const getAllRegions = () => {
  return [...new Set(indianFoodDb.map(food => food.region))];
};

export default indianFoodDb;
